import React from 'react';
import {View} from 'react-native';
import {PasswordInput} from 'react-native-pin-input-component';

class PasswordDefault extends React.Component {
  constructor() {
    super();
    this.state = {
      value: '',
    };
  }

  render() {
    const {value} = this.state;
    return (
      <View style={{marginTop: 100}}>
        <PasswordInput
          value={value}
          onChangeText={text => {
            this.setState({value: text});
          }}
          // cellAnimatedOnChangeText={scale => {
          //   Animated.timing(scale, {
          //     toValue: 1,
          //     duration: 250,
          //   }).start();
          //   // return scale.interpolate({
          //   //   inputRange: [0, 0.5, 1],
          //   //   outputRange: [1, 1.5, 1],
          //   // });
          //   return scale;
          // }}
        />
      </View>
    );
  }
}

export default PasswordDefault;
